import React, { useState, useEffect, useCallback } from 'react';
import './Task.css';

function Task() {
  const [todos, setTodos] = useState([]);
  const [inputText, setInputText] = useState('');
  const [filter, setFilter] = useState('all');
  const [priority, setPriority] = useState('all');

  useEffect(() => {
    getTodos();
  }, []);

  const filterHandler = useCallback(() => {
    let filteredTodos = todos;
    if (filter !== 'all') {
      filteredTodos = filteredTodos.filter(todo => 
        filter === 'completed' ? todo.completed : !todo.completed
      );
    }
    if (priority !== 'all') {
      filteredTodos = filteredTodos.filter(todo => todo.priority === priority);
    }
    return filteredTodos;
  }, [todos, filter, priority]);

  useEffect(() => {
    filterHandler();
  }, [todos, filter, priority, filterHandler]);

  const saveLocalTodos = useCallback(() => {
    localStorage.setItem('todos', JSON.stringify(todos));
  }, [todos]);

  useEffect(() => {
    saveLocalTodos();
  }, [todos, saveLocalTodos]);

  const handleInputChange = (e) => {
    setInputText(e.target.value);
  };

  const handleAddTodo = (e) => {
    e.preventDefault();
    setTodos([
      ...todos,
      { text: inputText, completed: false, priority: 'normal', id: Math.random() * 1000 }
    ]);
    setInputText('');
  };

  const handleDeleteTodo = (id) => {
    setTodos(todos.filter(todo => todo.id !== id));
  };

  const handleCompleteTodo = (id) => {
    setTodos(todos.map(todo => {
      if (todo.id === id) {
        return { ...todo, completed: !todo.completed };
      }
      return todo;
    }));
  };

  const handleFilterChange = (e) => {
    setFilter(e.target.value);
  };

  const handlePriorityChange = (e) => {
    setPriority(e.target.value);
  };

  const getTodos = () => {
    const savedTodos = JSON.parse(localStorage.getItem('todos')) || [];
    setTodos(savedTodos);
  };

  return (
    <div>
      <h1 className='text-center text-dark'>Task Manager</h1>
      <form>
        <div className="mainInput">
          <input 
            type="text" 
            className="todo-input" 
            value={inputText}
            onChange={handleInputChange}
          />
          <button className="todo-button" type="submit" onClick={handleAddTodo}>
            <i className="fas fa-plus-square"></i>
          </button>
        </div>
        <div className="prioInput">
          <div className="select">
            <select name="todos" className="filter-todo" onChange={handleFilterChange}>
              <option value="all">All</option>
              <option value="completed">Completed</option>
              <option value="uncompleted">Uncompleted</option>
            </select>
          </div>
          <div className="select">
            <select name="priority" className="filter-priority" onChange={handlePriorityChange}>
              <option value="all">All Priorities</option>
              <option value="low">Low</option>
              <option value="normal">Normal</option>
              <option value="high">High</option>
            </select>
          </div>
        </div>
      </form>
      <div className="todo-container">
        <ul className="todo-list">
          {filterHandler().map(todo => (
            <div className={`todo ${todo.priority}`} key={todo.id}>
              <li className={`todo-item ${todo.completed ? 'completed' : ''}`}>
                {todo.text}
              </li>
              <button onClick={() => handleCompleteTodo(todo.id)} className="complete-btn">
                <i className="fas fa-check"></i>
              </button>
              <button onClick={() => handleDeleteTodo(todo.id)} className="trash-btn">
                <i className="fas fa-trash"></i>
              </button>
            </div>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default Task;
